public void testIsNumberBlank() {
    assertFalse(NumberUtils.isNumber(" "));
    assertFalse(NumberUtils.isNumber(""));
    assertFalse(NumberUtils.isNumber("   "));
}

public void testEqualsNaN() {
    assertFalse(MathUtils.equals(Double.NaN, Double.NaN));
    assertFalse(MathUtils.equals(Float.NaN, Float.NaN));
}
